def largest_products_series(limit, series):
    # converts large number to a series of digits
    series_digits = [int(d) for d in str(series)]
    max_product = 1
    for index, digit in enumerate(series_digits):

        cut_series = series_digits[index:index+limit]
        product = product_single_digit_series(cut_series)

        if product > max_product:

            max_product = product

    return max_product

def product_single_digit_series(series_digits):

    product = 1
    for digit in series_digits:
        temp = product
        product *= digit
        if digit == 0:
            break
        print(str(product) + " = " + str(temp) + " x " + str(digit))

    return product

